function initSineWave(){const n=document.getElementById("broken-sine-wave");window.addEventListener("resize",(()=>{n.width=window.innerWidth}),!1),n.width=window.innerWidth,n.height=20;const e=n.getContext("2d");if(!e)return void console.log("No canvas :(");const i={y:window.innerHeight,length:10,amplitude:10,speed:.009};window.requestAnimationFrame((()=>animation(n,e,i,0)))}function animation(n,e,i,t){e.clearRect(0,0,n.width,n.height);const o={sx:0,sy:10,width:window.innerWidth,getY(n){return this.sy+Math.sin(n*i.length+t)*i.amplitude*Math.sin(t)}};plotSine(e,0,o),t+=i.speed,window.requestAnimationFrame((()=>animation(n,e,i,t)))}function plotSine(n,e,i){n.beginPath(),n.moveTo(i.sx+e,i.sy),n.strokeStyle="rgba(255, 255, 255, 0.5)",n.lineWidth=1;for(let t=i.sx;t<i.width;t++)n.lineTo(t+e,i.getY(t));n.stroke()}window.addEventListener("load",initSineWave,!1);